var fs = require('fs');
var exec = require('child_process').exec;
module.exports = init;
function init() {
	var dir = process.cwd();
	exec('git pull', function(err, stdout, stderr) {
		console.log('pull assets success!')
		if (err) {
			console.log(err);
		}
		console.log('stdout:' + stdout);
		console.log('stderr:' + stderr);
	});
}

